Get-ScheduledTask | ? { $_.TaskName -notmatch "GoogleUpdate|User_Feed_Synchronization|Optimize Start Menu" -and $_.Principal.UserId -ne "SYSTEM" -and $_.Author -and -not ($defaultAccounts -contains ($_.Author -split '\\')[-1]) } | % { $i = $_ | Get-ScheduledTaskInfo; [PSCustomObject] @{ TaskName = $_.TaskName; Actions = ($_.Actions | % { $_.Execute }) -join ', '; TaskPath = $_.TaskPath; RunAsAccount = $_.Principal.UserId; Author = $_.Author; Created = $_.Date; LastRunTime = $i.LastRunTime; NextRunTime = $i.NextRunTime; State = $_.State } } | Format-List
